export function getDrugTargetsForGene(gene: string): DrugTargetEntry[] {
  const upper = gene.toUpperCase();
  return DRUG_TARGET_DATABASE.filter(entry => entry.gene.toUpperCase() === upper);
}

export function getAllDrugTargetGenes(): string[] {
  const genes = new Set(DRUG_TARGET_DATABASE.map(entry => entry.gene));
  return Array.from(genes).sort();
}

export function getDrugTargetsByClass(drugClass: DrugClass): DrugTargetEntry[] {
  return DRUG_TARGET_DATABASE.filter(entry => entry.drugClass === drugClass);
}
